  option: (defaultStyles: any, state: any) => ({
    ...defaultStyles,
    fontSize: 14,
    marginLeft: 8
  }),

  control: (defaultStyles: any) => ({
    ...defaultStyles,
    fontFamily: 'Poppins',
    fontSize: 14,
