      <Select
        className="fs-5"
        inputId={name}
        name={name}
        options={options}
        isDisabled={readOnly}
        value={options.filter((option: any) => option.id === id)}
        onChange={onChange}
        styles={customStyles}
        getOptionLabel={({ name }) => name}
        getOptionValue={({ id }) => id}
