from flask import Flask, render_template, request, send_file
import cv2
import os
from werkzeug.utils import secure_filename
import uuid
import numpy as np

app = Flask(__name__)
app.config['UPLOAD_FOLDER'] = 'uploads/'

@app.route('/')
def index():
    return render_template('index.html')

@app.route('/upload', methods=['POST'])
def upload():
    if request.method == 'POST':
        image = request.files['image']
        filename = str(uuid.uuid4()) + secure_filename(image.filename)
        filepath = os.path.join(app.config['UPLOAD_FOLDER'], filename)
        image.save(filepath)

        return render_template('filters.html', original_image=filename)

@app.route('/uploads/<filename>')
def uploaded_file(filename):
    return send_file(os.path.join(app.config['UPLOAD_FOLDER'], filename))

@app.route('/apply_filter', methods=['POST'])
def apply_filter():
    if request.method == 'POST':
        original_image = request.form['original_image']
        filter_name = request.form['filter']

        filepath = os.path.join(app.config['UPLOAD_FOLDER'], original_image)
        image = cv2.imread(filepath)

        if filter_name == 'grayscale':
            filtered_image = cv2.cvtColor(image, cv2.COLOR_BGR2GRAY)
        elif filter_name == 'sepia':
            sepia_filter = np.array([[0.272, 0.534, 0.131],
                                     [0.349, 0.686, 0.168],
                                     [0.393, 0.769, 0.189]])
            filtered_image = cv2.transform(image, sepia_filter)
        elif filter_name == 'blur':
            filtered_image = cv2.GaussianBlur(image, (15, 15), 0)

        filtered_filename = f"filtered_{original_image}"
        filtered_filepath = os.path.join(app.config['UPLOAD_FOLDER'], filtered_filename)
        cv2.imwrite(filtered_filepath, filtered_image)

        return render_template('result.html',
                               original_image=original_image,
                               filtered_image=filtered_filename)

if __name__ == '__main__':
    app.run(debug=True)

Here's a C++ program that demonstrates the usage of all basic data types, their memory sizes, and ranges, along with custom classes for each basic data type:

```cpp
#include <iostream>
#include <limits>
#include <stdexcept>

template <typename T>
class CustomType {
public:
    CustomType() : value(0) {}
    explicit CustomType(T val) : value(val) {}

    ~CustomType() {}

    CustomType operator+(const CustomType& other) const {
        return CustomType(value + other.value);
    }

    CustomType operator-(const CustomType& other) const {
        return CustomType(value - other.value);
    }

    CustomType operator*(const CustomType& other) const {
        return CustomType(value * other.value);
    }

    CustomType operator/(const CustomType& other) const {
        if (other.value == 0) {
            throw std::runtime_error("Division by zero.");
        }
        return CustomType(value / other.value);
    }

    bool operator==(const CustomType& other) const {
        return value == other.value;
    }

    bool operator!=(const CustomType& other) const {
        return value != other.value;
    }

    bool operator<(const CustomType& other) const {
        return value < other.value;
    }

    bool operator>(const CustomType& other) const {
        return value > other.value;
    }

    bool operator<=(const CustomType& other) const {
        return value <= other.value;
    }

    bool operator>=(const CustomType& other) const {
        return value >= other.value;
    }

    friend std::ostream& operator<<(std::ostream& os, const CustomType& obj) {
        os << obj.value;
        return os;
    }

    friend std::istream& operator>>(std::istream& is, CustomType& obj) {
        is >> obj.value;
        return is;
    }

private:
    T value;
};

template <typename T>
void printTypeInfo(const std::string& typeName) {
    std::cout << typeName << ":\n";
    std::cout << "Size: " << sizeof(T) << " bytes\n";
    std::cout << "Range: " << std::numeric_limits<T>::min() << " to " << std::numeric_limits<T>::max() << "\n\n";
}

int main() {
    printTypeInfo<char>("char");
    printTypeInfo<short>("short");
    printTypeInfo<int>("int");
    printTypeInfo<long>("long");
    printTypeInfo<long long>("long long");
    printTypeInfo<float>("float");
    printTypeInfo<double>("double");
    printTypeInfo<long double>("long double");

    CustomType<int> a(5), b(3), c;
    c = a + b;
