class Account:
    def __init__(self, balance=0):
        self.balance = balance

    def deposit(self, amount):
        if amount > 0:
            self.balance += amount
            return True
        else:
            print("Invalid deposit amount.")
            return False

    def withdraw(self, amount):
        if amount <= self.balance:
            self.balance -= amount
            return True
        else:
            print("Insufficient funds.")
            return False

    def get_balance(self):
        return self.balance


class CheckingAccount(Account):
    def __init__(self, balance=0):
        super().__init__(balance)

    def transfer(self, to_account, amount):
        if self.withdraw(amount):
            to_account.deposit(amount)
            return True
        else:
            print("Transfer failed.")
            return False


class SavingsAccount(Account):
    def __init__(self, balance=0, interest_rate=0.01):
        super().__init__(balance)
        self.interest_rate = interest_rate

    def calculate_interest(self):
        return self.balance * self.interest_rate

    def apply_interest(self):
        interest = self.calculate_interest()
        self.deposit(interest)


# Example usage:

checking = CheckingAccount(1000)
savings = SavingsAccount(5000)

print("Checking balance:", checking.get_balance())
print("Savings balance:", savings.get_balance())

checking.transfer(savings, 200)
print("Transferred 200 from checking to savings")

print("Checking balance:", checking.get_balance())
print("Savings balance:", savings.get_balance())

savings.apply_interest()
print("Applied interest to savings account")
print("Savings balance:", savings.get_balance())
