def is_balanced(input_string):
    stack = []
    opening_symbols = "([{"
    closing_symbols = ")]}"
    matching_pairs = {")": "(", "]": "[", "}": "{"}

    for symbol in input_string:
        if symbol in opening_symbols:
            stack.append(symbol)
        elif symbol in closing_symbols:
            if not stack or stack.pop() != matching_pairs[symbol]:
                return False

    return len(stack) == 0


