        int destId = scanner.nextInt();
        System.out.print("Enter amount to transfer: ");
        double amount = scanner.nextDouble();

        // Withdraw from the source account
        String withdrawSql = "UPDATE accounts SET balance = balance - ? WHERE id = ?";
        // Deposit into the destination account
        String depositSql = "UPDATE accounts SET balance = balance + ? WHERE id = ?";

        try (PreparedStatement withdrawPstmt = conn.prepareStatement(withdrawSql);
             PreparedStatement depositPstmt = conn.prepareStatement(depositSql)) {

            conn.setAutoCommit(false);

            withdrawPstmt.setDouble(1, amount);
            withdrawPstmt.setInt(2, srcId);
            withdrawPstmt.executeUpdate();

            depositPstmt.setDouble(1, amount);
            depositPstmt.setInt(2, destId);
            depositPstmt.executeUpdate();

            conn.commit();
            System.out.println("Fund transfer successful!");

        } catch (SQLException e) {
            try {
                conn.rollback();
            } catch (SQLException ex) {
                System.err.println(ex.getMessage());
            }
            System.err.println(e.getMessage());
        } finally {
            try {
                conn.setAutoCommit(true);
            } catch (SQLException e) {
                System.err.println(e.getMessage());
            }
        }
    }
}

using System;
using MySql.Data.MySqlClient;
using System.Collections.Generic;

public class Product
{
    public int Id { get; set; }
    public string Name { get; set; }
    public string Description { get; set; }
    public decimal Price { get; set; }
    public int Quantity { get; set; }
}

public class DatabaseConnection
{
    private readonly string _connectionString;

    public DatabaseConnection(string connectionString)
    {
        _connectionString = connectionString;
    }

    // Get all products from the database.
    public List<Product> GetAllProducts()
    {
        var products = new List<Product>();

        using (var connection = new MySqlConnection(_connectionString))
        {
            connection.Open();

            using (var command = new MySqlCommand("SELECT * FROM products;", connection))
            using (var reader = command.ExecuteReader())
            {
                while (reader.Read())
                {
                    products.Add(new Product
                    {
                        Id = reader.GetInt32(0),
                        Name = reader.GetString(1),
                        Description = reader.GetString(2),
                        Price = reader.GetDecimal(3),
                        Quantity = reader.GetInt32(4)
                    });
                }
            }
        }

        return products;
    }

    // Add a new product to the database.
    public void AddProduct(Product product)
    {
        using (var connection = new MySqlConnection(_connectionString))
        {
            connection.Open();

            using (var command = new MySqlCommand("INSERT INTO products (name, description, price, quantity) VALUES (@Name, @Description, @Price, @Quantity);", connection))
            {
                command.Parameters.AddWithValue("@Name", product.Name);
                command.Parameters.AddWithValue("@Description", product.Description);
                command.Parameters.AddWithValue("@Price", product.Price);
                command.Parameters.AddWithValue("@Quantity", product.Quantity);

                command.ExecuteNonQuery();
            }
        }
    }

    // Update an existing product in the database.
    public void UpdateProduct(Product product)
    {
        using (var connection = new MySqlConnection(_connectionString))
        {
            connection.Open();

            using (var command = new MySqlCommand("UPDATE products SET name=@Name, description=@Description, price=@Price, quantity=@Quantity WHERE id=@Id;", connection))
            {
                command.Parameters.AddWithValue("@Id", product.Id);
                command.Parameters.AddWithValue("@Name", product.Name);
                command.Parameters.AddWithValue("@Description", product.Description);
                command.Parameters.AddWithValue("@Price", product.Price);
                command.Parameters.AddWithValue("@Quantity", product.Quantity);

                command.ExecuteNonQuery();
            }
        }
    }

    // Delete a product from the database by its ID.
    public void DeleteProduct(int id)
    {
        using (var connection = new MySqlConnection(_connectionString))
        {
            connection.Open();

            using (var command = new MySqlCommand("DELETE FROM products WHERE id=@Id;", connection))
            {
                command.Parameters.AddWithValue("@Id", id);
                command.ExecuteNonQuery();
            }
        }
    }
}

