            try {
                System.out.println("Processing: " + testCase);
                checkStringCriteria(testCase);
            } catch (CustomException e) {
                logger.severe("CustomException occurred: " + e.getMessage());
                System.out.println("Error: " + e.getMessage());
            } finally {
                System.out.println("Finished processing\n");
            }
        }
    }

    private static void checkStringCriteria(String input) throws CustomException {
        if (input == null) {
            throw new CustomException("Input should not be null");
        }

        if (input.length() < 5) {
            throw new CustomException("String length should be at least 5 characters");
        }

        if (!input.matches("[a-zA-Z0-9]+")) {
            throw new CustomException("String should only contain alphanumeric characters");
        }

        System.out.println("String '" + input + "' passed the criteria");
    }
}

class CustomException extends Exception {
    public CustomException(String message) {
        super(message);
    }
}
