from flask import Flask, render_template, request, send_from_directory
from PIL import Image
import os
from werkzeug.utils import secure_filename
import uuid

UPLOAD_FOLDER = 'uploads'
ALLOWED_EXTENSIONS = {'png', 'jpg', 'jpeg'}

app = Flask(__name__)
app.config['UPLOAD_FOLDER'] = UPLOAD_FOLDER


def allowed_file(filename):
    return '.' in filename and \
           filename.rsplit('.', 1)[1].lower() in ALLOWED_EXTENSIONS


@app.route('/')
def index():
    return render_template('index.html')


@app.route('/upload', methods=['POST'])
def upload_image():
    if 'file' not in request.files:
        return "No file part"

    file = request.files['file']

    if file.filename == '':
        return "No selected file"

    if file and allowed_file(file.filename):
        filename = secure_filename(file.filename)
        unique_id = str(uuid.uuid4())
        save_path = os.path.join(app.config['UPLOAD_FOLDER'], unique_id)
        os.makedirs(save_path)
        file.save(os.path.join(save_path, filename))
        return render_template('process.html', unique_id=unique_id, filename=filename)

    return "Invalid file type"


@app.route('/process', methods=['POST'])
def process_image():
    unique_id = request.form.get('unique_id')
    filename = request.form.get('filename')
    operation = request.form.get('operation')

    input_path = os.path.join(app.config['UPLOAD_FOLDER'], unique_id, filename)
    output_path = os.path.join(app.config['UPLOAD_FOLDER'], unique_id, f"processed_{filename}")

    img = Image.open(input_path)

    if operation == 'resize':
        width = int(request.form.get('width'))
        height = int(request.form.get('height'))
        img = img.resize((width, height))

    elif operation == 'crop':
        left = int(request.form.get('left'))
        upper = int(request.form.get('upper'))
        right = int(request.form.get('right'))
        lower = int(request.form.get('lower'))
        img = img.crop((left, upper, right, lower))

    elif operation == 'rotate':
        angle = int(request.form.get('angle'))
        img = img.rotate(angle)

    img.save(output_path)
    return send_from_directory(os.path.join(app.config['UPLOAD_FOLDER'], unique_id), f"processed_{filename}", as_attachment=True)


@app.route('/download/<path:filename>', methods=['GET'])
def download_image(filename):
    directory = os.path.join(app.config['UPLOAD_FOLDER'], filename.split('/')[0])
    return send_from_directory(directory, filename.split('/')[1], as_attachment=True)


if __name__ == '__main__':
    app.run(debug=True)

