	connectionString := flag.String("connection-string", "", "Azure Blob Storage connection string")
	downloadDir := flag.String("download-dir", ".", "Directory to download blobs to")

	flag.Parse()

	if *connectionString == "" {
		fmt.Println("Error: Connection string is required.")
		os.Exit(1)
	}

	accountInfo, err := azblob.NewSharedKeyCredentialFromConnectionString(*connectionString)
	if err != nil {
		fmt.Printf("Error parsing connection string: %v\n", err)
		os.Exit(1)
	}

	pipeline := azblob.NewPipeline(accountInfo, azblob.PipelineOptions{})
	serviceURL := azblob.NewServiceURL(accountInfo.AccountName(), pipeline)

	ctx := context.Background()
	containerListMarker := azblob.Marker{}
	for containerListMarker.NotDone() {
		listContainersResp, err := serviceURL.ListContainersSegment(ctx, containerListMarker, azblob.ListContainersOptions{})
		if err != nil {
			fmt.Printf("Error listing containers: %v\n", err)
			os.Exit(1)
		}

		for _, containerItem := range listContainersResp.ContainerItems {
			containerURL := serviceURL.NewContainerURL(containerItem.Name)
			blobListMarker := azblob.Marker{}

			for blobListMarker.NotDone() {
				listBlobsResp, err := containerURL.ListBlobsFlatSegment(ctx, blobListMarker, azblob.ListBlobsSegmentOptions{})
				if err != nil {
					fmt.Printf("Error listing blobs in container '%s': %v\n", containerItem.Name, err)
					os.Exit(1)
				}

				for _, blobItem := range listBlobsResp.Segment.BlobItems {
					blobURL := containerURL.NewBlobURL(blobItem.Name)
					resp, err := blobURL.Download(ctx, 0, azblob.CountToEnd, azblob.BlobAccessConditions{}, false)
					if err != nil {
						fmt.Printf("Error downloading blob '%s' from container '%s': %v\n", blobItem.Name, containerItem.Name, err)
						os.Exit(1)
					}

					bodyStream := resp.Body(azblob.RetryReaderOptions{})
					defer bodyStream.Close()

					data, err := ioutil.ReadAll(bodyStream)
					if err != nil {
						fmt.Printf("Error reading downloaded data for blob '%s' in container '%s': %v\n", blobItem.Name, containerItem.Name, err)
						os.Exit(1)
					}

					targetPath := filepath.Join(*downloadDir, containerItem.Name, blobItem.Name)
					err = os.MkdirAll(filepath.Dir(targetPath), 0755)
					if err != nil {
						fmt.Printf("Error creating directories for target path '%s': %v\n", targetPath, err)
						os.Exit(1)
					}

					err = ioutil.WriteFile(targetPath, data, 0644)
					if err != nil {
						fmt.Printf("Error writing downloaded data to file '%s': %v\n", targetPath, err)
						os.Exit(1)
					}

					fmt.Printf("Downloaded blob '%s' from container '%s' to '%s'\n", blobItem.Name, containerItem.Name, targetPath)
				}

				blobListMarker = listBlobsResp.NextMarker
			}
		}

		containerListMarker = listContainersResp.NextMarker
	}
}

