impl JournalSummary {
 fn from(journal: &Journal) -> Self {
 let mut oldest: NaiveDate = journal.transactions[0].date;

 for t in journal.transactions.iter() {
 if t.date < oldest {
 oldest = t.date;
 }
 }

 JournalSummary {
 oldest\_date: first\_day\_of\_month(oldest),
