#include <iostream>
#include <vector>
#include <cmath>

// Abstract base class for all shapes
class Shape {
public:
    virtual double area() const = 0;
    virtual double perimeter() const = 0;
};

// Circle class
class Circle : public Shape {
private:
    double radius;

public:
    Circle(double r) : radius(r) {}

    double area() const override {
        return M_PI * pow(radius, 2);
    }

    double perimeter() const override {
        return 2 * M_PI * radius;
    }
};

// Rectangle class
class Rectangle : public Shape {
private:
    double width;
    double height;

public:
    Rectangle(double w, double h) : width(w), height(h) {}

    double area() const override {
        return width * height;
    }

    double perimeter() const override {
        return 2 * (width + height);
    }
};

// Triangle class
class Triangle : public Shape {
private:
    double base;
    double height;

public:
    Triangle(double b, double h) : base(b), height(h) {}

    double area() const override {
        return 0.5 * base * height;
    }

    double perimeter() const override {
        // This is a simplification assuming equilateral triangle.
        // For more accurate calculation, we need the lengths of all sides.
        return 3 * base;
    }
};

// Function to calculate total area of a list of shapes
double totalArea(const std::vector<Shape*>& shapes) {
    double total = 0;
    for (const auto& shape : shapes) {
        total += shape->area();
    }
    return total;
}

int main() {
    Circle c(5);
    Rectangle r(4, 6);
    Triangle t(3, 7);

    std::vector<Shape*> shapes = { &c, &r, &t };

    std::cout << "Total area: " << totalArea(shapes) << std::endl;

    return 0;
}

import javax.swing.*;
import java.awt.*;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;

public class TicTacToe extends JFrame {
    private char currentSymbol;
    private JButton[] buttons;

    public TicTacToe() {
        currentSymbol = 'X';
        buttons = new JButton[9];
        setLayout(new GridLayout(3, 3));
        setDefaultCloseOperation(EXIT_ON_CLOSE);
        setSize(300, 300);

        for (int i = 0; i < 9; i++) {
            buttons[i] = new JButton("");
            buttons[i].addActionListener(new ActionListener() {
                @Override
                public void actionPerformed(ActionEvent e) {
                    JButton button = (JButton) e.getSource();
                    button.setText(String.valueOf(currentSymbol));

                    if (checkWinCondition()) {
                        JOptionPane.showMessageDialog(null,
                                "Player " + currentSymbol + " wins!");
                        resetGame();
                    } else {
                        switchSymbol();
                    }
                    button.setEnabled(false);
                }
            });
            add(buttons[i]);
        }

        setVisible(true);
    }

    private boolean checkWinCondition() {
        for (int i = 0; i < 3; i++) {
            if (checkRow(i) || checkColumn(i)) {
                return true;
            }
        }
        return checkDiagonal();
    }

    private boolean checkRow(int row) {
        int start = row * 3;
        return checkSequence(start, start+1, start+2);
    }

    private boolean checkColumn(int column) {
        return checkSequence(column, column+3, column+6);
    }

    private boolean checkDiagonal() {
        return checkSequence(0, 4, 8) || checkSequence(2, 4, 6);
    }

    private boolean checkSequence(int index1, int index2, int index3) {
        return buttons[index1].getText().equals(String.valueOf(currentSymbol))
                && buttons[index2].getText().equals(String.valueOf(currentSymbol))
                && buttons[index3].getText().equals(String.valueOf(currentSymbol));
    }

    private void switchSymbol() {
        currentSymbol = (currentSymbol == 'X') ? 'O' : 'X';
    }

    private void resetGame() {
        for (JButton button : buttons) {
            button.setText("");
            button.setEnabled(true);
        }
        currentSymbol = 'X';
    }

    public static void main(String[] args) {
        SwingUtilities.invokeLater(TicTacToe::new);
    }
}

